import { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import styled from 'styled-components'
import { InputField } from '../components/InputField'
import { fetchPhoneWordsAsync } from '../redux/actions'

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`

const Title = styled.h1`
  font-size: 3rem;
  color: #41444b;
`

const SubmitButton = styled.button`
  width: auto;
  background-color: #30d158;
  border-radius: 5px;
  color: #fff;
  cursor: pointer;
  font-size: 18px;
  font-weight: 500;
  height: 50px;
  padding: 0 1.5rem;
  text-align: center;
  white-space: nowrap;
  border: none;
  outline: none;
`

export const Home = () => {
  const [number, setNumber] = useState('')
  const [wordsList, setWordsList] = useState([])
  const dispatch = useDispatch()
  const data = useSelector(state => state.wordsData)

  // console.log(data)

  function handleSubmit () {
    dispatch(fetchPhoneWordsAsync(number))
    setWordsList(data)
  }

  console.log(wordsList)

  return (
    <Container>
      <Title>Phonewords Converter</Title>
      <form>
        <InputField
          name='number'
          value={number}
          placeholder='Only digits from 2 to 9'
          onChange={event => setNumber(event.target.value)}
        />
        <SubmitButton type='submit'>Submit</SubmitButton>
      </form>
      <ul>
        {wordsList
          ? wordsList.map((item, index) => <li key={index}>{item}</li>)
          : null}
      </ul>
    </Container>
  )
}
